import React, { useState } from "react";
import { FaBars, FaTimes } from "react-icons/fa";
import { Link } from "react-scroll";

const Navbar = () => {
  const [nav, setNav] = useState(false);

  const handleClick = () => setNav(!nav);

  return (
    <div className="fixed w-full h-[50px] flex justify-between items-center px-4 bg-[rgba(23,62,67,0.8)] text-[#FFFFFF]">
      {/* logo or brand name could go here */}

      {/* Desktop menu */}
      <ul className="hidden md:flex">{/* Desktop menu items */}</ul>

      {/* Hamburger */}
      <div onClick={handleClick} className="md:hidden z-10 cursor-pointer">
        {!nav ? <FaBars /> : <FaTimes />}
      </div>

      {/* Mobile menu */}
      <ul
        className={
          !nav
            ? "hidden"
            : "absolute top-0 left-0 w-full h-screen bg-[rgba(23,62,67,0.8)] flex flex-col justify-center items-center"
        }
      >
        <li className="py-6 text-4xl">
          <Link
            to="home"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Home
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="about"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            About
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="indexing-services"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Indexing Services
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="editing-services"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Editing Services
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="selected-clients"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Selected Clients
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="testimonials"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Testimonials
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="work-samples"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Work Samples
          </Link>
        </li>
        <li className="py-6 text-4xl">
          <Link
            to="contact"
            spy={true}
            smooth={true}
            duration={500}
            onClick={() => setNav(false)}
          >
            Contact
          </Link>
        </li>
      </ul>
    </div>
  );
};

export default Navbar;
